INSERT INTO `extras` (`id`, `title`, `text`, `icon`, `created_at`, `updated_at`) VALUES
(1, '{\"en\":\"Historical background\",\"ar\":\"\\u062e\\u0644\\u0641\\u064a\\u0629 \\u062a\\u0627\\u0631\\u064a\\u062e\\u064a\\u0629\"}', '{\"en\":\"We started working in training in 2011 AD as a training center, then we upgraded the center to an institute, which gives the opportunity for a longer training period of up to a full year, and our clients can obtain a one-year diploma in any of the disciplines provided by the institute.\",\"ar\":\"\\u0644\\u0642\\u062f \\u0628\\u062f\\u0623\\u0646\\u0627 \\u0627\\u0644\\u0639\\u0645\\u0644 \\u0641\\u064a \\u0627\\u0644\\u062a\\u062f\\u0631\\u064a\\u0628 \\u0639\\u0627\\u0645 2011\\u0645 \\u0643\\u0645\\u0631\\u0643\\u0632 \\u062a\\u062f\\u0631\\u064a\\u0628 \\u062b\\u0645 \\u0642\\u0645\\u0646\\u0627 \\u0628\\u062a\\u0631\\u0642\\u064a\\u0629 \\u0627\\u0644\\u0645\\u0631\\u0643\\u0632 \\u0627\\u0644\\u0649 \\u0645\\u0639\\u0647\\u062f \\u0648\\u0627\\u0644\\u0630\\u064a \\u064a\\u0639\\u0637\\u064a \\u0627\\u0644\\u0641\\u0631\\u0635\\u0629 \\u0644\\u0641\\u062a\\u0631\\u0629 \\u062a\\u062f\\u0631\\u064a\\u0628\\u064a\\u0629 \\u0623\\u0637\\u0648\\u0644 \\u062a\\u0635\\u0644 \\u0627\\u0644\\u0649 \\u0639\\u0627\\u0645 \\u0643\\u0627\\u0645\\u0644 \\u0648\\u0628\\u0625\\u0645\\u0643\\u0627\\u0646 \\u0639\\u0645\\u0644\\u0627\\u0626\\u0646\\u0627 \\u0627\\u0644\\u062d\\u0635\\u0648\\u0644 \\u0639\\u0644\\u0649 \\u062f\\u0631\\u062c\\u0629 \\u062f\\u0628\\u0644\\u0648\\u0645 \\u0644\\u0645\\u062f\\u0629 \\u0639\\u0627\\u0645 \\u0641\\u064a \\u0623\\u064a \\u0645\\u0646 \\u0627\\u0644\\u062a\\u062e\\u0635\\u0635\\u0627\\u062a \\u0627\\u0644\\u062a\\u064a \\u064a\\u0648\\u0641\\u0631\\u0647\\u0627 \\u0627\\u0644\\u0645\\u0639\\u0647\\u062f.\"}', 'images/extra-0GxBfZohM36jPZAr.png', '2023-01-03 17:47:52', '2023-01-03 17:47:52'),
(2, '{\"en\":\"Our vision\",\"ar\":\"\\u0631\\u0624\\u064a\\u062a\\u0646\\u0627\"}', '{\"en\":\"To be the pioneers in the field of training and to be one of the largest institutes not only in the Kingdom but also in the Middle East. Moreover, our goal is also to improve the institute\'s efficiency and profitability.\",\"ar\":\"\\u0623\\u0646 \\u0646\\u0643\\u0648\\u0646 \\u0627\\u0644\\u0631\\u0648\\u0627\\u062f \\u0641\\u064a \\u0645\\u062c\\u0627\\u0644 \\u0627\\u0644\\u062a\\u062f\\u0631\\u064a\\u0628 \\u0648\\u0623\\u0646 \\u0646\\u0643\\u0648\\u0646 \\u0648\\u0627\\u062d\\u062f \\u0645\\u0646 \\u0623\\u0643\\u0628\\u0631 \\u0627\\u0644\\u0645\\u0639\\u0627\\u0647\\u062f \\u0644\\u064a\\u0633 \\u0639\\u0644\\u0649 \\u0645\\u0633\\u062a\\u0648\\u0649 \\u0627\\u0644\\u0645\\u0645\\u0644\\u0643\\u0629 \\u0641\\u062d\\u0633\\u0628 \\u0628\\u0644 \\u0639\\u0644\\u0649 \\u0645\\u0633\\u062a\\u0648\\u0649 \\u0627\\u0644\\u0634\\u0631\\u0642 \\u0627\\u0644\\u0627\\u0648\\u0633\\u0637. \\u0648\\u0639\\u0644\\u0627\\u0648\\u0629 \\u0639\\u0644\\u0649 \\u0630\\u0644\\u0643 \\u0641\\u0647\\u062f\\u0641\\u0646\\u0627 \\u0623\\u064a\\u0636\\u0627 \\u0627\\u0644\\u0627\\u0631\\u062a\\u0642\\u0627\\u0621 \\u0628\\u0645\\u0633\\u062a\\u0648\\u0649 \\u0643\\u0641\\u0627\\u0621\\u0629 \\u0627\\u0644\\u0645\\u0639\\u0647\\u062f \\u0648\\u0631\\u0628\\u062d\\u064a\\u062a\\u0647.\"}', 'images/extra-pJ0WYhOFsVvTLBvb.png', '2023-01-03 17:48:27', '2023-01-03 17:48:27'),
(3, '{\"en\":\"Our message\",\"ar\":\"\\u0631\\u0633\\u0627\\u0644\\u062a\\u0646\\u0627\"}', '{\"en\":\"Providing high quality and distinguished training services to our clients in the Kingdom of Saudi Arabia and the Middle East, whether it is courses or training solutions. As well as using modern training methods and the latest specialized technologies, training and qualifying human resources while preserving the environment and public safety.\",\"ar\":\"\\u062a\\u0642\\u062f\\u064a\\u0645 \\u062e\\u062f\\u0645\\u0627\\u062a \\u062a\\u062f\\u0631\\u064a\\u0628\\u064a\\u0629 \\u0630\\u0627\\u062a \\u062c\\u0648\\u062f\\u0629 \\u0639\\u0627\\u0644\\u064a\\u0629 \\u0648\\u0645\\u062a\\u0645\\u064a\\u0632\\u0629 \\u0644\\u0639\\u0645\\u0644\\u0627\\u0626\\u0646\\u0627 \\u0641\\u064a \\u0627\\u0644\\u0645\\u0645\\u0644\\u0643\\u0629 \\u0627\\u0644\\u0639\\u0631\\u0628\\u064a\\u0629 \\u0627\\u0644\\u0633\\u0639\\u0648\\u062f\\u064a\\u0629 \\u0648\\u0627\\u0644\\u0634\\u0631\\u0642 \\u0627\\u0644\\u0623\\u0648\\u0633\\u0637 \\u0633\\u0648\\u0627\\u0621 \\u0643\\u0627\\u0646\\u062a \\u0627\\u0644\\u062f\\u0648\\u0631\\u0627\\u062a \\u0627\\u0648 \\u0627\\u0644\\u062d\\u0644\\u0648\\u0644 \\u0627\\u0644\\u062a\\u062f\\u0631\\u064a\\u0628\\u064a\\u0629. \\u0641\\u0636\\u0644\\u0627 \\u0639\\u0646 \\u0627\\u0633\\u062a\\u062e\\u062f\\u0627\\u0645 \\u0623\\u0633\\u0627\\u0644\\u064a\\u0628 \\u0627\\u0644\\u062a\\u062f\\u0631\\u064a\\u0628 \\u0627\\u0644\\u062d\\u062f\\u064a\\u062b\\u0629 \\u0648\\u0623\\u062d\\u062f\\u062b \\u0627\\u0644\\u062a\\u0642\\u0646\\u064a\\u0627\\u062a \\u0627\\u0644\\u0645\\u062a\\u062e\\u0635\\u0635\\u0629 \\u0648\\u062a\\u062f\\u0631\\u064a\\u0628 \\u0648\\u062a\\u0623\\u0647\\u064a\\u0644 \\u0627\\u0644\\u0645\\u0648\\u0627\\u0631\\u062f \\u0627\\u0644\\u0628\\u0634\\u0631\\u064a\\u0629 \\u0645\\u0639 \\u0627\\u0644\\u062d\\u0641\\u0627\\u0638 \\u0639\\u0644\\u0649 \\u0627\\u0644\\u0628\\u064a\\u0626\\u0629 \\u0648\\u0627\\u0644\\u0633\\u0644\\u0